import {React, useState} from "react"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {  faBars } from "@fortawesome/free-solid-svg-icons"
import '../css/navbar.css'

export default function Navbar(props){

    const { data , toggle } = props
    const [isMobile, toggleIsMobile] = useState(true)

    function printNav(){
        return (
            <ul className={isMobile ? "navigation-list" : "nav-list-mobile"}>
                {data.map(item => {
                    return (
                        <li className="navigation-item" key={item.id} onClick={() => toggle(false, item.id)}>{item.title}</li>)
                })}
            </ul>
        )
    }
    function css(element, styleToApply){
        for(const property in styleToApply)
            element.style[property] = styleToApply[property]
    }

    /** this function handles the openening the of the nav menu */
    function handleOnNavClick(){

        const styles = {
            display: "block",
            width: "100%",
            height: "100vh",
            position: "fixed",
            margin: "0 auto",
        }
        const nav = document.querySelector('.navigation-list')
        css(nav, styles)
        return(

            console.log("Menu Clicked", nav)
        )
    }


    return (
        <nav className="navigation">
            <img className="navigation-logo" src="../../images/mandala.png" alt="logo" onClick={() => toggle(true)} />
            <FontAwesomeIcon onClick={(handleOnNavClick)} className="navigation-bar" icon={faBars}/>
            {printNav()}
        </nav>
    )
}
