import React from 'react'
import '../css/cloud.css'

export default function Cloud(props) {

    const { sentence, id } = props
    console.log(props)

    function handleCloudClick() {
        const text = window.document.getElementById(`cloud${id}`)
        text.classList.remove('hidden')
    }

    return (
        <div className='cloud-container'>
            <div id={`cloud${id}`} className='expend hidden'>{sentence}</div>
            <div id='cloud' className='cloud'>
                <div className='cloud-text-wrapper' onClick={handleCloudClick}>
                    <p className='cloud-text'>{sentence}</p>
                </div>
            </div>
        </div >
    )
}
